import React, { useState } from 'react';
import '../styles/HeroSection.css';
import card from './Images/Card.png';
import google from './Images/google.png';
import twitch from './Images/twitch.png';
import facebook from './Images/Facebook.png';
import pinterest from './Images/Pinterest.png';
import YT from './Images/YT.png';

function HeroSection() {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        alert('Thank you for your submission!');
        closeModal();
    };

    return (
        <div className="hero">
            <div className="upper">
                <div className="head">Simplify Your Life with Our Todo App</div>
                <div className="body">
                    Stay organized and boost your productivity with our intuitive todo website. Experience a modern approach to task management that fits your lifestyle.
                </div>
                <div className="button">
                    <button className="btn1" onClick={openModal}>Get Started</button>
                    <button className="btn2">Learn More</button>
                </div>
            </div>

            <div className="lower">
                <div className="banner">
                    <p className="para1">Organize. Achieve. Relax.</p>
                    <p className="para2">Turn clutter into clarity, chaos into control, and dreams into done.</p>
                    <div>
                        <button className="btn5">Get Started Today</button>
                        <button className="btn6">Discover Features</button>
                    </div>
                </div>
                <div className="bannerimg">
                    <img src={card} alt="Banner" />
                </div>
            </div>

            <div className="socialmedia">
                <img src={google} alt="Google" />
                <img src={facebook} alt="Facebook" />
                <img src={YT} alt="YouTube" />
                <img src={pinterest} alt="Pinterest" />
                <img src={twitch} alt="Twitch" />
            </div>

            {isModalOpen && (
                <div className="modal">
                    <div className="modal-content">
                        <button className="close-btn" onClick={closeModal}>X</button>
                        <form onSubmit={handleSubmit}>
                            <label>
                                Name:
                                <input type="text" required />
                            </label>
                            <label>
                                Email:
                                <input type="email" required />
                            </label>
                            <label>
                                Phone Number:
                                <input type="tel" required />
                            </label>
                            <button type="submit">Submit</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}

export default HeroSection;
