import '../styles/CalltoAction.css'
import img from './Images/img.png'
import { useState } from 'react';
function CalltoAction(){
    const [isPopupVisible, setIsPopupVisible] = useState(false);
    const [formSubmitted, setFormSubmitted] = useState(false);
    

      const handleGetStartedClick = () => {
        setIsPopupVisible(true);
        document.body.style.overflow = 'hidden'; 
      };
    

      const closePopup = () => {
        setIsPopupVisible(false);
        document.body.style.overflow = 'auto'; 
      };
       const handleSubmit = (e) => {
      e.preventDefault();
      setFormSubmitted(true); 
    };
return(
    <div className='container'>
        <div>
            <img className='img' src={img}></img>
        </div>
        <div className='content1'>
            <p className='p1'>Start Organizing Your Life Today</p>
            <p className='para'> Join us now and transform your productivity with our intuitive to-do list platform!</p>
           <div className='buttons'>
                <button className='btn3' onClick={handleGetStartedClick}>Sign Up</button>
                <button className='btn4'>Learn More</button>
           </div>
        </div>
          {isPopupVisible && (
        <div className="popup-overlay">
          <div className="popup">
            <button className="close-btn" onClick={closePopup}>X</button>

            {/* Check if form is submitted or not */}
            {!formSubmitted ? (
              <>
                <h2>Get Started Today!</h2>
                <p>Fill in your details and take control of your tasks.</p>
                <form className="popup-form" onSubmit={handleSubmit}>
                  <div>
                    <label htmlFor="first-name">First Name:</label>
                    <input type="text" id="first-name" name="first-name" required />
                  </div>
                  <div>
                    <label htmlFor="last-name">Last Name:</label>
                    <input type="text" id="last-name" name="last-name" required />
                  </div>
                  <div>
                    <label htmlFor="email">Email:</label>
                    <input type="email" name="email" required />
                  </div>
                  <div>
                    <label htmlFor="password">Password:</label>
                    <input type="password" id="password" name="password" required />
                  </div>
                  <button type="submit">Done</button>
                </form>
              </>
            ) : (
              <>
                <h2>Thank You!</h2>
                <p>Thank you for connecting with us. <br />Our team will be contacting you soon.</p>
                <button onClick={closePopup} className='done'>Done</button>
              </>
            )}
          </div>
        </div>
      )}
    </div>
)
}
export default CalltoAction;
