import React, { useState } from 'react';
import '../styles/HeroSection.css';
import card from './Images/Card.png';
import google from './Images/google.png';
import twitch from './Images/twitch.png';
import facebook from './Images/Facebook.png';
import pinterest from './Images/Pinterest.png';
import YT from './Images/YT.png';

// Import the new TodoListFeature component
import TodoListFeature from './TodolistFeatures';

function HeroSection() {
  // State to control which view is displayed
  const [isViewVisible, setIsViewVisible] = useState('home'); // Default view is 'home'

  // Function to handle the "Get Started" button click
  const handleGetStartedClick = () => {
    setIsViewVisible('todo');  // Show TodoListFeature view after clicking the button
  };

  // Function to go back to the "home" view
  const handleBackToHome = () => {
    setIsViewVisible('home');  // Go back to the home view
  };

  return (
    <div className='hero'>
      {/* Conditionally render views */}
      {isViewVisible === 'home' ? (
        <div className='upper'>
          <div className='head'>Simplify Your Life with Our Todo App</div>
          <div className='body'>
            Stay organized and boost your productivity with our intuitive todo website. Experience a modern approach to task management that fits your lifestyle.
          </div>
          <div className='button'>
            <button className='btn1' onClick={handleGetStartedClick}>Get Started</button>
            <button className='btn2' onClick={handleGetStartedClick}>Learn More</button>
          </div>
        </div>
      ) : isViewVisible === 'todo' ? (
        <TodoListFeature />
      ) : null}

      {/* Other content for the HeroSection */}
      {isViewVisible === 'home' && (
        <div className='lower'>
          <div className='banner'>
            <p className='para1'>Organize. Achieve. Relax.</p>
            <p className='para2'>
              Turn clutter into clarity, chaos into control, and dreams into done.bold visions into market success
            </p>
            <div>
              <button className='btn5' onClick={handleGetStartedClick}>Get Started Today</button>
              <button className='btn6' onClick={handleGetStartedClick}>Discover Features</button>
            </div>
          </div>
          <div className='bannerimg'>
            <img src={card} alt="card" />
          </div>
        </div>
      )}

      {/* Social Media Icons */}
      <div className='socialmedia'>
        <img src={google} alt="google" />
        <img src={facebook} alt="facebook" />
        <img src={YT} alt="YT" />
        <img src={pinterest} alt="pinterest" />
        <img src={twitch} alt="twitch" />
      </div>
    </div>
  );
}

export default HeroSection;
