import React, { useEffect, useState } from 'react';
import todolist from './Images/Todolist.png';
import '../styles/Header.css';

function Header() {
 const [isPopupVisible, setIsPopupVisible] = useState(false);

  // Function to handle "Get Started" button click
  const handleGetStartedClick = () => {
    setIsPopupVisible(true);
    document.body.style.overflow = 'hidden'; // Prevent scrolling when popup is open
  };

  // Function to close the popup
  const closePopup = () => {
    setIsPopupVisible(false);
    document.body.style.overflow = 'auto'; // Enable scrolling again
  };


  return (
    <div className='main2' >
      <img src={todolist} alt="ToDo List" className="logo" />
      <div className='header'>
        <div className="nav-item">About Us</div>
        <div className="nav-item">Features</div>
        <div className="nav-item">More Option</div>
        <div className="nav-item">Contact</div>
        <div className="auth-buttons">
          <button className="login-btn">Log In</button>
          <button className="signup-btn" onClick={handleGetStartedClick}>Sign Up</button>
        </div>
      </div>
      {isPopupVisible && (
        <div className='popup-overlay'>
          <div className='popup'>
            <button className='close-btn' onClick={closePopup}>X</button>
            <h2>Get Started Today!</h2>
            <h5>Fill in your details and take control of your tasks.</h5>
            <form className='popup-form'>
              <div>
                <label htmlFor='name'>First Name:</label>
                <input type="text" id='name' name="name" required />
                 <label htmlFor='name'>Last Name:</label>
                <input type="text" id='name' name="name" required />
              </div>
              <div>
                <label htmlFor='email'>Email:</label>
                <input type="email" id='email' name="email" required />
              </div>
              <div>
                <label htmlFor='password'>Password:</label>
                <input type="password" id='password' name="password" required />
              </div>
              <button type="submit">Done</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Header;
