import '../styles/CalltoAction.css'
import img from './Images/img.png'
import { useState } from 'react';
function CalltoAction(){
    const [isPopupVisible, setIsPopupVisible] = useState(false);
    
      // Function to handle "Get Started" button click
      const handleGetStartedClick = () => {
        setIsPopupVisible(true);
        document.body.style.overflow = 'hidden'; // Prevent scrolling when popup is open
      };
    
      // Function to close the popup
      const closePopup = () => {
        setIsPopupVisible(false);
        document.body.style.overflow = 'auto'; // Enable scrolling again
      };
return(
    <div className='container'>
        <div>
            <img className='img' src={img}></img>
        </div>
        <div className='content1'>
            <p className='p1'>Start Organizing Your Life Today</p>
            <p className='para'> Join us now and transform your productivity with our intuitive to-do list platform!</p>
           <div className='buttons'>
                <button className='btn3' onClick={handleGetStartedClick}>Sign Up</button>
                <button className='btn4'>Learn More</button>
           </div>
        </div>
         {isPopupVisible && (
        <div className='popup-overlay'>
          <div className='popup'>
            <button className='close-btn' onClick={closePopup}>X</button>
            <h2>Sign Up</h2>
            <form className='popup-form'>
              <div>
                <label htmlFor='name'>Full Name:</label>
                <input type="text" id='name' name="name" required />
              </div>
              <div>
                <label htmlFor='email'>Email:</label>
                <input type="email" id='email' name="email" required />
              </div>
              <div>
                <label htmlFor='password'>Password:</label>
                <input type="password" id='password' name="password" required />
              </div>
              <button type="submit">Done</button>
            </form>
          </div>
        </div>
      )}
    </div>
)
}
export default CalltoAction;
