import React, { useEffect, useState } from 'react';
import todolist from './Images/Todolist.png';
import '../styles/Header.css';

function Header() {
  const [showHeader, setShowHeader] = useState(true);
  const [lastScrollTop, setLastScrollTop] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop;

      // Show header when scrolling down or at top
      if (scrollTop < 20) {
        setShowHeader(true);
      } else if (scrollTop > lastScrollTop) {
        setShowHeader(true); // Scrolling down
      } else {
        setShowHeader(false); // Scrolling up
      }

      setLastScrollTop(scrollTop);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [lastScrollTop]);

  return (
    <div className={`main2 fixed-header ${showHeader ? 'show' : 'hide'}`}>
      <img src={todolist} alt="ToDo List" className="logo" />
      <div className='header'>
        <div className="nav-item">About Us</div>
        <div className="nav-item">Features</div>
        <div className="nav-item">More Option</div>
        <div className="nav-item">Contact</div>
        <div className="auth-buttons">
          <button className="login-btn">Log In</button>
          <button className="signup-btn">Sign Up</button>
        </div>
      </div>
      {isPopupVisible && (
        <div className='popup-overlay'>
          <div className='popup'>
            <button className='close-btn' onClick={closePopup}>X</button>
            <h2>Sign Up</h2>
            <form className='popup-form'>
              <div>
                <label htmlFor='name'>Full Name:</label>
                <input type="text" id='name' name="name" required />
              </div>
              <div>
                <label htmlFor='email'>Email:</label>
                <input type="email" id='email' name="email" required />
              </div>
              <div>
                <label htmlFor='password'>Password:</label>
                <input type="password" id='password' name="password" required />
              </div>
              <button type="submit">Done</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Header;
