import '../styles/CalltoAction.css';
import img from './Images/img.png';
import { useState } from 'react';

function CalltoAction() {
  const [isPopupVisible, setIsPopupVisible] = useState(false);
  const [formSubmitted, setFormSubmitted] = useState(false);

  // Function to handle "Get Started" button click (opens the popup)
  const handleGetStartedClick = () => {
    setIsPopupVisible(true);
    document.body.style.overflow = 'hidden'; // Prevent scrolling when popup is open
  };

  // Function to close the popup
  const closePopup = () => {
    setIsPopupVisible(false);
    setFormSubmitted(false); // Reset form submission state when closing the popup
    document.body.style.overflow = 'auto'; // Enable scrolling again
  };

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setFormSubmitted(true); // Set form submission state to true
  };

  return (
    <div className="container">
      <div>
        <img className="img" src={img} alt="image" />
      </div>
      <div className="content1">
        <p className="p1">Start Organizing Your Life Today</p>
        <p className="para">
          Join us now and transform your productivity with our intuitive to-do list platform!
        </p>
        <div className="buttons">
          <button className="btn3" onClick={handleGetStartedClick}>
            Sign Up
          </button>
          <button className="btn4">Learn More</button>
        </div>
      </div>

      {/* Popup Overlay */}
      {isPopupVisible && (
        <div className="popup-overlay">
          <div className="popup">
            <button className="close-btn" onClick={closePopup}>
              X
            </button>

            {/* Check if form is submitted or not */}
            {!formSubmitted ? (
              <>
                <h2>Sign Up</h2>
                <form className="popup-form" onSubmit={handleSubmit}>
                  <div>
                    <label htmlFor="name">Full Name:</label>
                    <input type="text" id="name" name="name" required />
                  </div>
                  <div>
                    <label htmlFor="email">Email:</label>
                    <input type="email" id="email" name="email" required />
                  </div>
                  <div>
                    <label htmlFor="password">Password:</label>
                    <input type="password" id="password" name="password" required />
                  </div>
                  <button type="submit">Done</button>
                </form>
              </>
            ) : (
              <>
                <h2>Thank You!</h2>
                <p>
                  Thank you for connecting with us. <br />
                  Our team will be contacting you soon.
                </p>
                <button onClick={closePopup} className="done">
                  Done
                </button>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default CalltoAction;
