import React, { useState } from 'react';
import '../styles/CalltoAction.css';
import img from './Images/img.png';

function CalltoAction() {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        alert('Thank you for your submission!');
        closeModal();
    };

    return (
        <div className="container">
            <div>
                <img className="img" src={img} alt="Call to Action" />
            </div>
            <div className="content1">
                <p className="p1">Start Organizing Your Life Today</p>
                <p className="para">Join us now and transform your productivity with our intuitive to-do list platform!</p>
                <div className="buttons">
                    <button className="btn3" onClick={openModal}>Sign Up</button>
                    <button className="btn4">Learn More</button>
                </div>
            </div>

            {isModalOpen && (
                <div className="modal">
                    <div className="modal-content">
                        <button className="close-btn" onClick={closeModal}>X</button>
                        <form onSubmit={handleSubmit}>
                            <label>
                                Name:
                                <input type="text" required />
                            </label>
                            <label>
                                Email:
                                <input type="email" required />
                            </label>
                            <label>
                                Phone Number:
                                <input type="tel" required />
                            </label>
                            <button type="submit">Submit</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}

export default CalltoAction;
